/**
 * Checks if a value is a plain object.
 *
 * Based on https://github.com/sindresorhus/is-plain-obj.
 *
 * An object is plain if it is created by either:
 * - {}
 * - new Object()
 * - Object.create(null)
 *
 * @template A
 * @param {A} x
 * @returns {boolean}
 */
export function isPlainObject<A>(x: A): boolean;
