export type Plugin = import('fela').TPlugin;
/**
 * @typedef {import('fela').TPlugin} Plugin
 */
/**
 * Fela plugin to handle responsive values.
 *
 * @param {object} props
 * @param {string[]} props.breakpoints
 * @param {string[]} props.responsiveCssProperties
 * @returns {Plugin}
 */
export function responsiveValue({ breakpoints, responsiveCssProperties }: {
    breakpoints: string[];
    responsiveCssProperties: string[];
}): Plugin;
